import { useReducer, useState, useRef } from "react";
//init State
const initState = 0;
//actions
const UP_ACTION = 'up';
const DOWN_ACTION = 'down'
//reducer nhan 2 tham so truyen vao no se dua vao action roi se update state thanh state moi
const reducer = (state, action) => {
    switch (action) {
        case UP_ACTION:
            {
                return state += 1;

            }
        case DOWN_ACTION:
            {
                return state -= 1;

            }
        default: throw new Error('Action error')
    }
}
// bai 41 todo useReducer
const initTodo = {
    job: '',
    jobs: []
}

const ADD_JOB = 'add_job';
const SET_JOB = 'set_job';
const DELETE_JOB = 'delete_job';
const setJob = payload => {
    return {
        type: SET_JOB,
        payload
    }
}
const addJob = payload => {
    return {
        type: ADD_JOB,
        payload
    }
}
const deleteJob = payload => {
    return {
        type: DELETE_JOB,
        payload
    }
}

console.log(setJob())
const reducerTodo = (state, action) => {
    switch (action.type) {
        case SET_JOB: {
            return {
                ...state, job: action.payload
            }
        }
        case ADD_JOB: {
            return {
                ...state, jobs: [...state.jobs, action.payload]
            }
        }
        case DELETE_JOB: {
            const newJob = [...state.jobs];
            newJob.slice(action.payload, 1);
            return {
                ...state, jobs: newJob
            }
        }
        default: new Error('Error')
    }
    return state;
}
function UseReducer() {

    const [count, dispatch] = useReducer(reducer, initState)
    const [state, dispatchTodo] = useReducer(reducerTodo, initTodo)
    const { job, jobs } = state
    const handleSubmit = () => {
        dispatchTodo(addJob(job))
        dispatchTodo(setJob(''))
        inputRef.current.focus()
    }
    const inputRef = useRef()

    return (
        <>
            <h2>{count}</h2>
            <button onClick={() => { dispatch(UP_ACTION) }}>Up</button>
            <button onClick={() => { dispatch(DOWN_ACTION) }}>Down</button>
            <div></div>
            <br></br>
            <h3>Todo</h3>
            <input placeholder="Enter Todo..." value={job} onChange={e => { dispatchTodo(setJob(e.target.value)) }} ref={inputRef} />
            <button onClick={handleSubmit}>Add</button>
            <ul>
                {jobs.map((item, index) => (
                    <li key={index}>{item} <span onClick={() => { dispatchTodo(deleteJob(index)) }}> &times;</span></li>
                ))}


            </ul>
        </>
    )
}
export default UseReducer;
