const UP_ACTION = 'up';
const DOWN_ACTION = 'down'
//reducer nhan 2 tham so truyen vao no se dua vao action roi se update state thanh state moi
const reducer = (state, action) => {
    switch (action) {
        case UP_ACTION:
            {
                return state += 1;

            }
        case DOWN_ACTION:
            {
                return state -= 1;

            }
        default: throw new Error('Action error')
    }
}
