import { useEffect, useState } from "react";
function Avatar() {
    const [avatar, setAvatar] = useState('');
    useEffect(() => {
        return () => {
            avatar && URL.revokeObjectURL(avatar.preview)
        }
    }, [avatar])
    const handleAvatar = (e) => {
        console.log("duong dan", URL.createObjectURL(e));
        setAvatar(URL.createObjectURL(e));

    }
    return (
        <>
            <input type="file" onChange={(event) => handleAvatar(event.target.files[0])}>

            </input>
            <div>
                {avatar && (<img src={avatar}></img>)}
            </div>
        </>
    )
}
export default Avatar;
