import { useState } from "react";
function ToDoList() {
    const [jobs, setJobs] = useState([]);
    const handlerSubmit = () => {
        console.log(document.getElementById('demo').innerHTML)
    }
    return (
        <>
            <input id="demo" /> <button onClick={handlerSubmit}>Add</button>
            <ul>
                {jobs.map((item, index) => (
                    <li key={index}>{item}</li>
                ))}

            </ul>
        </>
    )
}
export default ToDoList;
