import { useEffect, useState } from "react";
const lessons = [
    {
        id: 1,
        name: 'ReactJS la gi? Tai sao nen hoc'
    }, {
        id: 2,
        name: 'SPA/MPA la gi'
    }, {
        id: 3,
        name: 'Arrow function'
    }
]

function ChatApp() {
    const [lessonId, setLessonId] = useState(1);
    return (
        <>
            {lessons.map(less => (
                <div key={less.id} onClick={() => setLessonId(less.id)} style={{ color: less.id === lessons.id ? 'red' : 'black' }}>{less.name}</div>
            ))}
        </>
    )
}
export default ChatApp;
