import { useState, useLayoutEffect } from "react";
import { useEffect } from "react/cjs/react.development";
function Content() {
    const [count, setCount] = useState(1);
    useEffect(() => {
        if (count > 3) {
            setCount(1);
        }
    }, [count])
    const handleCount = () => {
        setCount(count + 1);

    }
    return (
        <>
            <p>{count}</p>
            <button onClick={handleCount}>Click</button>
        </>
    )
}

export default Content;
