import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import App from './App';
import reportWebVitals from './reportWebVitals';
import PhanThuong from './baiTap26/phanThuong';
//fake comments
emitComment = (id) => {
  setInterval(() => {
    // tao su kien tu comment sau 2s
    // su kien trong dom khac con khi muon tu tao event thi dung doi tuong windown
    window.dispatchEvent(
      new CustomEvent(`lesson-${id}`, { detail: `Noi dung comment cua lesson ${id}` })
    )
  }, 2000);
}
ReactDOM.render(
  <React.StrictMode>
    <App />
    {/* <PhanThuong /> */}
