import { useState } from "react";
const gifts = [
    'CPU I9',
    'RAM 32G RGB',
    'RGB KeyBoard'
]
function phanThuong() {
    const [gift, setGift] = useState(gifts[0]);
    return (
        <>
            <p>{gift || 'Chưa Có Phần Thưởng'}</p>
            <button>Lấy Thưởng</button>
        </>
    )
}
export default phanThuong;
